  }

  return <form onSubmit={formHandler} className={styles['user-form']}>
      <div className="group">
        <label htmlFor="username">Username</label>
        <input onChange={usernameHandler} type="text" name="username-input" id="usernameInput" value={text} />
      </div>
      <div className="group">
        <label htmlFor="age">Age (Years)</label>
        <input onChange={ageHandler} type="number" name="age-input" id="ageInput" value={age} />
      </div>
      <Button type='submit'>Add User</Button>
  </form>;
};

export default UserForm;
