import React, {useState} from 'react';
import Button from './UI/Button';

import styles from './UserForm.module.css'

const UserForm = () => {

  const [text, setText] = useState('dsadadas')
  const [age, setAge] = useState(0)

  const textHandler = e => {
    setText(e.target.value)
    console.log(e.target.value)
  }

  const ageHandler = e => {
    setAge(e.target.value)
    console.log(e.target.value)
  }

  return <form className={styles['user-form']}>
      <div className="group">
        <label htmlFor="username">Username</label>
        <input onChange={textHandler} type="text" name="username-input" id="usernameInput" value={text} />
      </div>
      <div className="group">
        <label htmlFor="age">Age (Years)</label>
        <input onClick={ageHandler} type="number" name="age-input" id="ageInput" value={age} />
      </div>
      <Button type='submit'>Add User</Button>
  </form>;
};

export default UserForm;
